-- INSERT INTO candles VALUES (NULL, 91, 'Floral', 'Wedding Day®', 'https://www.yankeecandle.com/yankee-candle/candles/candle-styles/original-jar-candles/wedding-day/ORCL_115438.html', 'This is a Floral fragrance. Always in good taste...a sophisticated and soothing blend of florals and subtle fruits. Top notes: Sheer Citrus, Aldehydes, Greens. Middle notes: Mimosa, Ylang, Jasmine, Rose, Tuberose. Base notes: Sweet Musk, Sandalwood, Tonka Bean. Top note is the initial impression of the fragrance, middle note is the main body of the scent and base is its final impression.', 4.5, 466, 'One of my favorites for the past 25 years.  Even burned several of these during our wedding so I love to burn it and go back to that day.  This is a sophisticated scent.', 5);
-- INSERT INTO candles VALUES (NULL, 91, 'Floral', 'Wedding Day®', 'https://www.yankeecandle.com/yankee-candle/candles/candle-styles/original-jar-candles/wedding-day/ORCL_115438.html', 'This is a Floral fragrance. Always in good taste...a sophisticated and soothing blend of florals and subtle fruits. Top notes: Sheer Citrus, Aldehydes, Greens. Middle notes: Mimosa, Ylang, Jasmine, Rose, Tuberose. Base notes: Sweet Musk, Sandalwood, Tonka Bean. Top note is the initial impression of the fragrance, middle note is the main body of the scent and base is its final impression.', 4.5, 466, 'This is one of my favorite Yankee candle gifts. I bought two of them for two granddaughters that are getting married this spring. I was disappointed that I didn’t get the notice about adding a picture until after I had already ordered.', 5);
